import requests

url = "https://api.prismacloud.io/code/api/v2/dashboard/iac-errors-by-category"

payload = {
    "repositories": ["string"],
    "severities": ["INFO"],
    "size": 0
}
headers = {
    "content-type": "application/json",
    "authorization": "REPLACE_KEY_VALUE"
}

response = requests.request("POST", url, json=payload, headers=headers)

print(response.text)

