import requests

url = "https://api.prismacloud.io/code/api/v1/tag-rules"

payload = {
    "description": "string",
    "isEnabled": True,
    "name": "string",
    "repositories": ["string"],
    "ruleDefinition": {"tag_groups": [
            {
                "name": "string",
                "tags": [
                    {
                        "name": "string",
                        "value": {
                            "default": "string",
                            "matches": [
                                {
                                    "property1": {"tags": {
                                            "property1": "string",
                                            "property2": "string"
                                        }},
                                    "property2": {"tags": {
                                            "property1": "string",
                                            "property2": "string"
                                        }}
                                }
                            ]
                        }
                    }
                ]
            }
        ]},
    "tagRuleOOTBId": "string"
}
headers = {
    "content-type": "application/json",
    "authorization": "REPLACE_KEY_VALUE"
}

response = requests.request("POST", url, json=payload, headers=headers)

print(response.text)

